INSERT INTO `answers` (`id`, `question_id`, `text`, `explanation`, `is_correct`, `created_at`, `updated_at`) VALUES
	(1, 1, 'Para aumentar el rendimiento', 'Los switches VLAN segmentan el tráfico, reduciendo colisiones.', 1, '2025-11-24 14:05:53', '2025-11-24 14:05:53'),
	(2, 1, 'Para mejorar la estética', 'No tiene relación con redes.', 0, '2025-11-24 14:05:53', '2025-11-24 14:05:53'),
	(3, 1, 'Para ahorrar energía', 'No es la función de una VLAN.', 0, '2025-11-24 14:05:53', '2025-11-24 14:05:53'),
	(4, 1, 'Porque se ve más profesional', 'Tampoco es un motivo técnico.', 0, '2025-11-24 14:05:53', '2025-11-24 14:05:53'),
	(5, 11, 'Aplicar reglas de rate-limiting y activar protección en el WAF/firewall', 'Limitar la tasa y activar reglas automáticas en WAF/firewall reduce el tráfico malicioso sin afectar usuarios legítimos.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(6, 11, 'Reiniciar el servidor web inmediatamente', 'Reiniciar no detiene el flujo malicioso y solo interrumpe servicio; no resuelve la causa.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(7, 11, 'Bloquear IPs manualmente una por una', 'En un DDoS las IPs son muchas y/o spoofed; el bloqueo manual es ineficiente.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(8, 11, 'Cambiar el dominio del sitio', 'Cambiar dominio no evita que el objetivo sea atacado por IPs; no es solución inmediata.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(9, 12, 'Buscar bucles de red y revisar Spanning Tree (STP)', 'Los bucles generan tormentas de broadcast; verificar cables y STP suele resolverlo.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(10, 12, 'Formatear las máquinas de la VLAN', 'Los endpoints no suelen causar broadcast storms; formatear es extremo e innecesario.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(11, 12, 'Cambiar el ISP', 'El ISP no interviene en broadcast locales dentro de la VLAN.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(12, 12, 'Aumentar el MTU en los switches', 'MTU no afecta tormentas de broadcast; no es la acción correcta.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(13, 13, 'Verificar permisos y pertenencia al grupo en el servidor de archivos', 'A menudo el problema es de permisos de usuario o su grupo; comprobar permisos es el primer paso.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(14, 13, 'Reiniciar todo el dominio', 'Reiniciar componentes del dominio sin diagnóstico perjudica a muchos usuarios; no es primera opción.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(15, 13, 'Reinstalar el sistema operativo del cliente', 'Extremo y poco probable que resuelva un problema de permisos de red.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(16, 13, 'Cambiar la IP pública del servidor', 'La IP pública no afecta recursos compartidos internos en la LAN.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(17, 14, 'Revisar y reemplazar el cable y probar en otro puerto/switch', 'Problemas físicos de cable o puerto son causa común; probar y reemplazar es la acción correcta.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(18, 14, 'Formatear el equipo del usuario', 'Innecesario si el problema es físico; no es la primera acción.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(19, 14, 'Actualizar el antivirus del equipo', 'El antivirus no suele causar desconexiones físicas en el cable.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(20, 14, 'Deshabilitar la tarjeta de red y dejarla deshabilitada', 'No resuelve la necesidad de conectividad; no es solución.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(21, 15, 'Crear bloqueos temporales y configurar alertas en IDS/IPS hacia esa IP', 'Bloquear y alertar reduce riesgo inmediato y permite analizar el origen; es buen primer paso.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(22, 15, 'Eliminar cuentas de usuario sin investigar', 'Eliminar usuarios sin investigar puede causar problemas operativos y perder información.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(23, 15, 'Desconectar todo el datacenter', 'Acción extrema que afecta operaciones; no es proporcional al problema.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(24, 15, 'Cambiar todas las contraseñas de la empresa', 'Acción masiva innecesaria inicialmente; investiga y aplica bloqueos y alertas primero.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(25, 16, 'Comprobar canales y posible fuente de interferencia (microondas, AP vecinos)', 'Interferencias por canal o dispositivos cercanos son causas frecuentes; ajustar canales o potencia ayuda.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(26, 16, 'Reinstalar el controlador de dominio', 'El controlador no regula la interferencia RF; no es lo primero a revisar.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(27, 16, 'Abrir todos los puertos en el firewall', 'Abrir puertos no mejora la señal WiFi; es irrelevante.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(28, 16, 'Sustituir todos los APs por otros más caros', 'Puede ser solución costosa y no necesaria sin diagnóstico previo.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(29, 17, 'Revisar y depurar la configuración de rutas (BGP/OSPF) y eliminar rutas erróneas', 'Problemas de configuración o procesos de enrutamiento pueden crear rutas duplicadas; depurar la configuración es clave.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(30, 17, 'Reiniciar el servidor web', 'Esto no tiene relación con las tablas de enrutamiento.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(31, 17, 'Aumentar MTU de todas las interfaces', 'MTU no resolverá rutas duplicadas; es una acción incorrecta.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(32, 17, 'Deshabilitar el enrutamiento dinámico por completo', 'Deshabilitar dinámico puede detener la redundancia necesaria; no es la primera medida.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(33, 18, 'Comprobar que no existan servidores DHCP duplicados y revisar reservas y scopes', 'Dos servidores DHCP o scopes solapados causan duplicados; verificar servidores y reservas es lo primero.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(34, 18, 'Apagar switches al azar', 'Apagar switches no resuelve conflictos DHCP y perjudica la red.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(35, 18, 'Cambiar el nombre del dominio', 'No tiene relación con DHCP ni con conflictos de IP.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(36, 18, 'Formatear los equipos afectados', 'Accion extrema y sin relación con la asignación de IP.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(37, 19, 'Aislar y desconectar el dispositivo, documentar MAC/IP y notificar al responsable', 'Aislar evita riesgos inmediatos; documentar y notificar permite investigación y remediación.', 1, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(38, 19, 'Cambiar todas las claves del WiFi inmediatamente', 'Puede ser necesario según investigación, pero primero aislar el dispositivo.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(39, 19, 'Encender más puntos de acceso para confundir', 'No tiene sentido desde seguridad; es contraproducente.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(40, 19, 'Ignorar y esperar a ver si desaparece', 'Ignorar expone la red a riesgo; no es una opción responsable.', 0, '2025-11-24 14:14:52', '2025-11-24 14:14:52'),
	(41, 20, 'Bloquear el puerto y revisar logs para identificar el origen del tráfico', 'Bloquear el puerto inutilizado y analizar los logs es la acción correcta para evitar explotación y comprender el intento.', 1, '2025-11-24 14:16:38', '2025-11-24 14:16:38'),
	(42, 20, 'Abrir el puerto para ver si deja de fallar', 'Abrir el puerto aumenta la superficie de ataque y es peligroso.', 0, '2025-11-24 14:16:38', '2025-11-24 14:16:38'),
	(43, 20, 'Migrar todos los servicios internos a una VLAN distinta', 'Mover servicios no resuelve el problema del tráfico sospechoso; es una acción innecesaria.', 0, '2025-11-24 14:16:38', '2025-11-24 14:16:38'),
	(44, 20, 'Apagar el servidor principal por precaución', 'Medida extrema que detiene operaciones sin necesidad; no mitiga el tráfico sospechoso.', 0, '2025-11-24 14:16:38', '2025-11-24 14:16:38'),
	(45, 21, 'Crear un índice sobre las columnas usadas en WHERE y JOIN', 'Los índices reducen drásticamente los tiempos de búsqueda en tablas grandes.', 1, '2025-11-24 14:18:24', '2025-11-24 14:18:24'),
	(46, 21, 'Eliminar registros antiguos de la tabla', 'Puede servir a largo plazo, pero no resuelve el problema de búsqueda inmediata.', 0, '2025-11-24 14:18:24', '2025-11-24 14:18:24'),
	(47, 21, 'Reiniciar el servidor de base de datos', 'No resuelve la falta de índices.', 0, '2025-11-24 14:18:24', '2025-11-24 14:18:24'),
	(48, 21, 'Aumentar la RAM del servidor', 'Mejora el rendimiento, pero no soluciona el cuello de botella por falta de índices.', 0, '2025-11-24 14:18:24', '2025-11-24 14:18:24'),
	(49, 22, 'Revisar y optimizar el orden de bloqueo en las transacciones', 'Los deadlocks se reducen cuando todas las transacciones siguen un orden consistente de bloqueo.', 1, '2025-11-24 14:18:31', '2025-11-24 14:18:31'),
	(50, 22, 'Desactivar el motor InnoDB temporalmente', 'InnoDB es necesario para transacciones, no debe desactivarse.', 0, '2025-11-24 14:18:31', '2025-11-24 14:18:31'),
	(51, 22, 'Aumentar el timeout global de la base de datos', 'Solo oculta el problema, no lo soluciona.', 0, '2025-11-24 14:18:31', '2025-11-24 14:18:31'),
	(52, 22, 'Borrar la tabla y recrearla', 'Extremadamente destructivo e innecesario.', 0, '2025-11-24 14:18:31', '2025-11-24 14:18:31'),
	(53, 23, 'Restaurar la tabla desde un backup consistente reciente', 'Cuando hay corrupción real, restaurar desde un backup es la acción más segura.', 1, '2025-11-24 14:18:42', '2025-11-24 14:18:42'),
	(54, 23, 'Ejecutar DELETE en todas las filas con error', 'Podrías perder información valiosa sin reparar el problema real.', 0, '2025-11-24 14:18:42', '2025-11-24 14:18:42'),
	(55, 23, 'Ignorar la advertencia y seguir operando', 'La corrupción puede escalar y comprometer toda la base.', 0, '2025-11-24 14:18:42', '2025-11-24 14:18:42'),
	(56, 23, 'Cambiar el motor de almacenamiento de inmediato', 'No soluciona corrupción ya existente.', 0, '2025-11-24 14:18:42', '2025-11-24 14:18:42'),
	(57, 24, 'Configurar expiración automática de binlogs con expire_logs_days o binlog_expire_logs_seconds', 'Controla el tamaño de los binlogs de forma segura.', 1, '2025-11-24 14:18:53', '2025-11-24 14:18:53'),
	(58, 24, 'Eliminar todos los binlogs manualmente sin revisar', 'Podrías romper la replicación si existe.', 0, '2025-11-24 14:18:53', '2025-11-24 14:18:53'),
	(59, 24, 'Reiniciar el servidor para liberar espacio', 'Reiniciar no reduce el tamaño de los binlogs.', 0, '2025-11-24 14:18:53', '2025-11-24 14:18:53'),
	(60, 24, 'Desactivar los binlogs para siempre', 'No es recomendable si usas replicación o recuperación punto en el tiempo.', 0, '2025-11-24 14:18:53', '2025-11-24 14:18:53'),
	(61, 25, 'Revocar inmediatamente los privilegios innecesarios', 'Seguridad básica: principio del menor privilegio.', 1, '2025-11-24 14:18:58', '2025-11-24 14:18:58'),
	(62, 25, 'Ignorarlo porque todo parece funcionar', 'Un riesgo de seguridad innecesario.', 0, '2025-11-24 14:18:58', '2025-11-24 14:18:58'),
	(63, 25, 'Eliminar al usuario del sistema operativo', 'No soluciona el riesgo en la base de datos.', 0, '2025-11-24 14:18:58', '2025-11-24 14:18:58'),
	(64, 25, 'Reiniciar el servidor para “limpiar” privilegios', 'No cambia permisos.', 0, '2025-11-24 14:18:58', '2025-11-24 14:18:58'),
	(65, 26, 'Verificar si el servidor replica tiene recursos saturados (CPU, disco, IO)', 'Un slave saturado procesa eventos más lento que el master.', 1, '2025-11-24 14:19:03', '2025-11-24 14:19:03'),
	(66, 26, 'Reiniciar el master', 'No suele solucionar el retraso.', 0, '2025-11-24 14:19:03', '2025-11-24 14:19:03'),
	(67, 26, 'Eliminar los binlogs antiguos del master', 'Podrías romper la replicación.', 0, '2025-11-24 14:19:03', '2025-11-24 14:19:03'),
	(68, 26, 'Desactivar la replicación temporalmente', 'No soluciona el retraso, solo lo acumula.', 0, '2025-11-24 14:19:03', '2025-11-24 14:19:03'),
	(69, 27, 'Identificar y finalizar la sesión que mantiene el bloqueo', 'Finalizar la sesión problemática libera los bloqueos.', 1, '2025-11-24 14:19:10', '2025-11-24 14:19:10'),
	(70, 27, 'Reiniciar todo el servidor', 'Exagerado y con impacto innecesario.', 0, '2025-11-24 14:19:10', '2025-11-24 14:19:10'),
	(71, 27, 'Eliminar todas las filas de la tabla bloqueada', 'Destructivo y absurdo.', 0, '2025-11-24 14:19:10', '2025-11-24 14:19:10'),
	(72, 27, 'Modificar el motor de almacenamiento de la tabla', 'No resuelve un bloqueo activo.', 0, '2025-11-24 14:19:10', '2025-11-24 14:19:10'),
	(73, 28, 'Seleccionar solo las columnas necesarias', 'Reduce tráfico, mejora performance y claridad.', 1, '2025-11-24 14:19:15', '2025-11-24 14:19:15'),
	(74, 28, 'Crear índices en todas las columnas', 'No resuelve el problema del SELECT *.', 0, '2025-11-24 14:19:15', '2025-11-24 14:19:15'),
	(75, 28, 'Duplicar la tabla para optimizar velocidad', 'No tiene sentido.', 0, '2025-11-24 14:19:15', '2025-11-24 14:19:15'),
	(76, 28, 'Cambiar el motor de almacenamiento a memoria', 'Medida extrema y sin relación.', 0, '2025-11-24 14:19:15', '2025-11-24 14:19:15'),
	(77, 29, 'Configurar una estrategia de backups automatizados y probados', 'Los backups deben existir, automatizarse y verificarse.', 1, '2025-11-24 14:19:21', '2025-11-24 14:19:21'),
	(78, 29, 'Eliminar registros antiguos para evitar llenar el disco', 'No sustituye a los backups.', 0, '2025-11-24 14:19:21', '2025-11-24 14:19:21'),
	(79, 29, 'Apagar la base hasta tener backup', 'No es práctico ni necesario.', 0, '2025-11-24 14:19:21', '2025-11-24 14:19:21'),
	(80, 29, 'Esperar a que haya tiempo para hacerlo', 'Deja al sistema vulnerable.', 0, '2025-11-24 14:19:21', '2025-11-24 14:19:21'),
	(81, 30, 'Desplegar un hotfix inmediato tras test rápido', 'Un hotfix controlado permite resolver el problema sin comprometer más usuarios.', 1, '2025-11-24 14:26:31', '2025-11-24 14:26:31'),
	(82, 30, 'Ignorar y esperar al siguiente release', 'El bug afecta a usuarios, ignorarlo empeora la situación.', 0, '2025-11-24 14:26:31', '2025-11-24 14:26:31'),
	(83, 30, 'Revertir toda la aplicación a cero', 'Extremo y disruptivo.', 0, '2025-11-24 14:26:31', '2025-11-24 14:26:31'),
	(84, 30, 'Aumentar el logging y no hacer nada más', 'Necesario, pero no resuelve el problema de inmediato.', 0, '2025-11-24 14:26:31', '2025-11-24 14:26:31'),
	(85, 31, 'Resolver manualmente los conflictos revisando cuidadosamente los cambios', 'Siempre verifica los cambios antes de mergear para no romper funcionalidad.', 1, '2025-11-24 14:26:37', '2025-11-24 14:26:37'),
	(86, 31, 'Forzar merge con --theirs', 'Podrías perder tu trabajo.', 0, '2025-11-24 14:26:37', '2025-11-24 14:26:37'),
	(87, 31, 'Eliminar la rama y crear otra', 'No resuelve el conflicto, solo elimina trabajo.', 0, '2025-11-24 14:26:37', '2025-11-24 14:26:37'),
	(88, 31, 'Ignorar el conflicto y seguir trabajando', 'No se puede ignorar, Git no permitirá commit.', 0, '2025-11-24 14:26:37', '2025-11-24 14:26:37'),
	(89, 32, 'Escribir pruebas unitarias antes de cambiar el código', 'Siempre cubrir código crítico con tests antes de modificarlo.', 1, '2025-11-24 14:26:43', '2025-11-24 14:26:43'),
	(90, 32, 'Refactorizar directamente sin pruebas', 'Arriesgado: puedes romper funcionalidad existente.', 0, '2025-11-24 14:26:43', '2025-11-24 14:26:43'),
	(91, 32, 'Pedir aprobación sin tests', 'No garantiza seguridad del código.', 0, '2025-11-24 14:26:43', '2025-11-24 14:26:43'),
	(92, 32, 'Ignorar las pruebas y documentar cambios', 'No asegura estabilidad del código.', 0, '2025-11-24 14:26:43', '2025-11-24 14:26:43'),
	(93, 33, 'Revisar los errores reportados por CI y corregirlos', 'CI/CD reporta problemas que deben solucionarse antes del merge.', 1, '2025-11-24 14:26:48', '2025-11-24 14:26:48'),
	(94, 33, 'Ignorar los errores y mergear de todos modos', 'Rompería la rama principal.', 0, '2025-11-24 14:26:48', '2025-11-24 14:26:48'),
	(95, 33, 'Desactivar temporalmente CI', 'No recomendado, se pierde control de calidad.', 0, '2025-11-24 14:26:48', '2025-11-24 14:26:48'),
	(96, 33, 'Pedir a otro desarrollador que mergee', 'No resuelve el problema.', 0, '2025-11-24 14:26:48', '2025-11-24 14:26:48'),
	(97, 34, 'Optimizar el uso de memoria y revisar algoritmos', 'Reducir consumo y mejorar eficiencia es la solución correcta.', 1, '2025-11-24 14:26:52', '2025-11-24 14:26:52'),
	(98, 34, 'Reiniciar el servidor', 'No resuelve el problema del script en sí.', 0, '2025-11-24 14:26:52', '2025-11-24 14:26:52'),
	(99, 34, 'Dividir el script en partes más pequeñas sin revisar lógica', 'Puede ayudar, pero primero optimiza.', 0, '2025-11-24 14:26:52', '2025-11-24 14:26:52'),
	(100, 34, 'Ignorar el error y continuar', 'No es viable.', 0, '2025-11-24 14:26:52', '2025-11-24 14:26:52'),
	(101, 35, 'Revisar versiones compatibles y actualizar de forma controlada', 'Actualizar de manera controlada evita romper la aplicación.', 1, '2025-11-24 14:26:59', '2025-11-24 14:26:59'),
	(102, 35, 'Eliminar todas las librerías y reinstalar', 'Riesgoso y puede generar más problemas.', 0, '2025-11-24 14:26:59', '2025-11-24 14:26:59'),
	(103, 35, 'Ignorar y seguir usando versiones antiguas', 'No resuelve conflictos futuros.', 0, '2025-11-24 14:26:59', '2025-11-24 14:26:59'),
	(104, 35, 'Actualizar solo en producción sin tests', 'Muy arriesgado.', 0, '2025-11-24 14:26:59', '2025-11-24 14:26:59'),
	(105, 36, 'Perfilar y optimizar el código o la consulta', 'Medir antes de cambiar asegura que optimizas correctamente.', 1, '2025-11-24 14:27:04', '2025-11-24 14:27:04'),
	(106, 36, 'Eliminar la función', 'Destructivo y rompe la app.', 0, '2025-11-24 14:27:04', '2025-11-24 14:27:04'),
	(107, 36, 'Aumentar el hardware sin revisar el código', 'Solo parche temporal, no soluciona la raíz.', 0, '2025-11-24 14:27:04', '2025-11-24 14:27:04'),
	(108, 36, 'Ignorar que es lenta', 'Afecta a los usuarios y reputación.', 0, '2025-11-24 14:27:04', '2025-11-24 14:27:04'),
	(109, 37, 'Implementar validación y sanitización de todos los inputs', 'Previene errores y ataques (inyección, XSS, etc.)', 1, '2025-11-24 14:27:08', '2025-11-24 14:27:08'),
	(110, 37, 'Depender solo de la base de datos para validar', 'No es suficiente.', 0, '2025-11-24 14:27:08', '2025-11-24 14:27:08'),
	(111, 37, 'Ignorar y confiar en el cliente', 'Muy inseguro.', 0, '2025-11-24 14:27:08', '2025-11-24 14:27:08'),
	(112, 37, 'Registrar los inputs maliciosos pero no bloquearlos', 'Solo detecta, no previene.', 0, '2025-11-24 14:27:08', '2025-11-24 14:27:08'),
	(113, 38, 'Realizar un backup antes del deploy', 'Siempre respaldar antes de cambios críticos en producción.', 1, '2025-11-24 14:27:12', '2025-11-24 14:27:12'),
